import { useState, useEffect } from 'react';
import { supabase } from '../services/supabase';
import { Link } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';

export default function ReviewsSection({ ideaId, authorId, user }) {
    const [reviews, setReviews] = useState([]);
    const [rating, setRating] = useState(5);
    const [content, setContent] = useState('');
    const [replyContent, setReplyContent] = useState({}); // Stores replies per review ID
    const [loading, setLoading] = useState(true);
    const [submitting, setSubmitting] = useState(false);
    const [replyingTo, setReplyingTo] = useState(null);
    const [error, setError] = useState('');
    const [userHasReviewed, setUserHasReviewed] = useState(false);
    const isAuthor = user?.id === authorId;

    useEffect(() => {
        fetchReviews();
    }, [ideaId]);

    const fetchReviews = async () => {
        setLoading(true);
        const { data, error } = await supabase
            .from('income_idea_reviews')
            .select('*, profiles(id, full_name, avatar_url)')
            .eq('idea_id', ideaId)
            .order('created_at', { ascending: false });

        if (!error && data) {
            setReviews(data);
            const reviewed = data.find(r => r.user_id === user?.id);
            setUserHasReviewed(!!reviewed);
        }
        setLoading(false);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!user) return;

        setSubmitting(true);
        setError('');

        const { error } = await supabase
            .from('income_idea_reviews')
            .insert([{
                user_id: user.id,
                idea_id: ideaId,
                rating,
                content
            }]);

        if (error) {
            if (error.code === '23505') {
                setError('You have already reviewed this blueprint.');
            } else {
                setError('Transmission failed. Ensure the blueprint is active.');
            }
        } else {
            setContent('');
            setRating(5);
            fetchReviews();
        }
        setSubmitting(false);
    };

    const handlePostReply = async (reviewId) => {
        const reply = replyContent[reviewId];
        if (!reply?.trim()) return;

        setSubmitting(true);
        const { error } = await supabase
            .from('income_idea_reviews')
            .update({
                author_response: reply,
                responded_at: new Date().toISOString()
            })
            .eq('id', reviewId);

        if (!error) {
            setReplyContent(prev => ({ ...prev, [reviewId]: '' }));
            setReplyingTo(null);
            fetchReviews();
        } else {
            console.error('Reply error:', error);
            alert('Failed to transmit reply to the community grid.');
        }
        setSubmitting(false);
    };

    const handleDelete = async (reviewId) => {
        if (!confirm('Are you sure you want to retract your review?')) return;

        const { error } = await supabase
            .from('income_idea_reviews')
            .delete()
            .eq('id', reviewId);

        if (!error) {
            fetchReviews();
        }
    };

    return (
        <div className="card space-y-8 bg-white/50 backdrop-blur-sm border-charcoal-100">
            <h3 className="text-xl font-black text-charcoal-900 flex items-center gap-3">
                <span>üí¨</span> Community Intel
            </h3>

            {user ? (
                !userHasReviewed ? (
                    <form onSubmit={handleSubmit} className="space-y-6 pb-8 border-b border-charcoal-50">
                        <div className="space-y-4">
                            <label className="text-[10px] font-black text-charcoal-400 uppercase tracking-widest pl-1">Operational Rating</label>
                            <div className="flex gap-2">
                                {[1, 2, 3, 4, 5].map((s) => (
                                    <button
                                        key={s}
                                        type="button"
                                        onClick={() => setRating(s)}
                                        className={`text-2xl transition-all ${s <= rating ? 'grayscale-0 scale-110' : 'grayscale opacity-30 hover:opacity-50'}`}
                                    >
                                        ‚≠ê
                                    </button>
                                ))}
                            </div>
                        </div>

                        <div className="space-y-2">
                            <label className="text-[10px] font-black text-charcoal-400 uppercase tracking-widest pl-1">Strategic Feedback</label>
                            <textarea
                                required
                                value={content}
                                onChange={(e) => setContent(e.target.value)}
                                className="w-full px-5 py-4 bg-white border border-charcoal-100 rounded-2xl focus:ring-2 focus:ring-primary-600 outline-none text-sm font-medium min-h-[100px] resize-none"
                                placeholder="Share your experience with this blueprint..."
                            />
                        </div>

                        {error && <p className="text-red-500 text-xs font-bold">{error}</p>}

                        <button
                            type="submit"
                            disabled={submitting}
                            className="btn-primary w-full py-4 text-xs tracking-widest uppercase font-black"
                        >
                            {submitting ? 'Transmitting...' : 'Post Intel'}
                        </button>
                    </form>
                ) : (
                    <div className="bg-primary-50 p-6 rounded-3xl border border-primary-100 text-center mb-8">
                        <p className="text-primary-700 text-sm font-bold uppercase tracking-widest mb-1">Intel Logged</p>
                        <p className="text-primary-600 text-xs font-medium opacity-80">You've successfully shared your feedback on this asset.</p>
                    </div>
                )
            ) : (
                <div className="bg-charcoal-50 p-8 rounded-3xl text-center border border-charcoal-100 mb-8">
                    <p className="text-charcoal-600 font-bold text-sm mb-4">Login to contribute operational feedback.</p>
                    <Link to="/login" className="btn-secondary py-2 px-6 text-[10px]">Secure Access</Link>
                </div>
            )}

            <div className="space-y-6">
                {loading ? (
                    <div className="py-12 flex justify-center">
                        <div className="w-6 h-6 border-2 border-primary-600 border-t-transparent rounded-full animate-spin"></div>
                    </div>
                ) : reviews.length === 0 ? (
                    <div className="py-12 text-center text-charcoal-400 italic text-sm font-medium">
                        No community intel available for this asset yet.
                    </div>
                ) : (
                    reviews.map((review) => (
                        <motion.div
                            key={review.id}
                            initial={{ opacity: 0, y: 10 }}
                            animate={{ opacity: 1, y: 0 }}
                            className="group p-6 bg-white rounded-[2rem] border border-charcoal-50 hover:border-primary-100 transition-all shadow-sm hover:shadow-md"
                        >
                            <div className="flex justify-between items-start mb-4">
                                <Link
                                    to={`/profile/${review.profiles?.id}`}
                                    className="flex items-center gap-3"
                                >
                                    <div className="w-10 h-10 rounded-xl overflow-hidden bg-charcoal-50 border border-charcoal-100">
                                        {review.profiles?.avatar_url ? (
                                            <img src={review.profiles.avatar_url} className="w-full h-full object-cover" alt="" />
                                        ) : (
                                            <div className="w-full h-full flex items-center justify-center font-black text-primary-600">
                                                {review.profiles?.full_name?.charAt(0)}
                                            </div>
                                        )}
                                    </div>
                                    <div>
                                        <div className="text-[10px] font-black text-charcoal-900 uppercase tracking-widest leading-none mb-1 group-hover:text-primary-600 transition-colors">
                                            {review.profiles?.full_name}
                                        </div>
                                        <div className="flex gap-0.5">
                                            {[...Array(review.rating)].map((_, i) => (
                                                <span key={i} className="text-[10px]">‚≠ê</span>
                                            ))}
                                        </div>
                                    </div>
                                </Link>
                                <div className="flex items-start gap-3">
                                    <div className="text-[8px] font-black text-charcoal-300 uppercase tracking-widest mt-1">
                                        {new Date(review.created_at).toLocaleDateString('en-IN', { day: 'numeric', month: 'short' })}
                                    </div>
                                    {user?.id === review.user_id && (
                                        <button
                                            onClick={() => handleDelete(review.id)}
                                            className="text-charcoal-300 hover:text-red-500 transition-colors"
                                        >
                                            <svg className="w-3.5 h-3.5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M6 18L18 6M6 6l12 12" />
                                            </svg>
                                        </button>
                                    )}
                                </div>
                            </div>
                            <p className="text-sm text-charcoal-600 font-medium leading-relaxed italic pr-4 mb-4">
                                "{review.content}"
                            </p>

                            {/* Author Response Section */}
                            {review.author_response ? (
                                <div className="mt-4 pl-6 border-l-2 border-primary-100 py-1">
                                    <div className="flex items-center gap-2 mb-2">
                                        <span className="text-[8px] font-black bg-primary-600 text-white px-2 py-0.5 rounded-full uppercase tracking-widest">Blueprint Author</span>
                                        <span className="text-[8px] font-black text-charcoal-300 uppercase tracking-widest">
                                            {new Date(review.responded_at).toLocaleDateString('en-IN', { day: 'numeric', month: 'short' })}
                                        </span>
                                    </div>
                                    <p className="text-sm text-charcoal-900 font-bold leading-relaxed">
                                        {review.author_response}
                                    </p>
                                </div>
                            ) : (
                                isAuthor && replyingTo !== review.id && (
                                    <button
                                        onClick={() => setReplyingTo(review.id)}
                                        className="text-[10px] font-black text-primary-600 uppercase tracking-widest hover:underline flex items-center gap-1.5"
                                    >
                                        <span>‚Ü©Ô∏è Reply as Author</span>
                                    </button>
                                )
                            )}

                            {/* Reply Input (Author Only) */}
                            <AnimatePresence>
                                {replyingTo === review.id && (
                                    <motion.div
                                        initial={{ opacity: 0, height: 0 }}
                                        animate={{ opacity: 1, height: 'auto' }}
                                        exit={{ opacity: 0, height: 0 }}
                                        className="mt-4 pt-4 border-t border-charcoal-50"
                                    >
                                        <textarea
                                            value={replyContent[review.id] || ''}
                                            onChange={(e) => setReplyContent(prev => ({ ...prev, [review.id]: e.target.value }))}
                                            className="w-full px-4 py-3 bg-charcoal-50 border border-charcoal-100 rounded-xl focus:ring-2 focus:ring-primary-600 outline-none text-xs font-medium min-h-[80px] resize-none"
                                            placeholder="Write your response to the commander..."
                                        />
                                        <div className="flex gap-2 mt-2 justify-end">
                                            <button
                                                onClick={() => setReplyingTo(null)}
                                                className="px-4 py-2 text-[10px] font-black text-charcoal-400 uppercase tracking-widest hover:text-charcoal-900"
                                            >
                                                Cancel
                                            </button>
                                            <button
                                                onClick={() => handlePostReply(review.id)}
                                                disabled={submitting}
                                                className="px-6 py-2 bg-charcoal-900 text-white rounded-lg text-[10px] font-black uppercase tracking-widest hover:bg-primary-600 transition-colors shadow-lg"
                                            >
                                                {submitting ? 'Sending...' : 'Post Response'}
                                            </button>
                                        </div>
                                    </motion.div>
                                )}
                            </AnimatePresence>
                        </motion.div>
                    ))
                )}
            </div>
        </div>
    );
}

import { createContext, useContext, useEffect, useState } from 'react';
import { authService, supabase } from '../services/supabase';

const AuthContext = createContext({});

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [profile, setProfile] = useState(null);
    const [session, setSession] = useState(null);
    const [loading, setLoading] = useState(true);

    const fetchProfile = async (userId) => {
        const { data } = await supabase
            .from('profiles')
            .select('*')
            .eq('id', userId)
            .single();

        if (data) setProfile(data);
    };

    useEffect(() => {
        // Get initial session
        authService.getSession().then(({ session }) => {
            setSession(session);
            const currentUser = session?.user ?? null;
            setUser(currentUser);
            if (currentUser) fetchProfile(currentUser.id);
            setLoading(false);
        });

        // Listen for auth changes
        const {
            data: { subscription },
        } = authService.onAuthStateChange((_event, session) => {
            setSession(session);
            const currentUser = session?.user ?? null;
            setUser(currentUser);
            if (currentUser) fetchProfile(currentUser.id);
            else setProfile(null);
            setLoading(false);
        });

        return () => {
            subscription?.unsubscribe();
        };
    }, []);

    const value = {
        user,
        profile,
        session,
        loading,
        refreshProfile: () => user && fetchProfile(user.id),
        signUp: authService.signUp,
        signIn: authService.signIn,
        signOut: authService.signOut,
        resetPassword: authService.resetPassword,
        updatePassword: authService.updatePassword,
    };

    return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

import { createContext, useContext, useEffect, useState } from 'react';

const ThemeContext = createContext();

export function ThemeProvider({ children }) {
    const [isDark, setIsDark] = useState(() => {
        // Check local storage or system preference
        if (typeof window !== 'undefined') {
            const stored = localStorage.getItem('theme');
            if (stored) return stored === 'dark';
            return window.matchMedia('(prefers-color-scheme: dark)').matches;
        }
        return false;
    });

    useEffect(() => {
        const root = window.document.documentElement;
        if (isDark) {
            root.classList.add('dark');
            localStorage.setItem('theme', 'dark');
        } else {
            root.classList.remove('dark');
            localStorage.setItem('theme', 'light');
        }
    }, [isDark]);

    const toggleTheme = () => setIsDark(!isDark);

    return (
        <ThemeContext.Provider value={{ isDark, toggleTheme }}>
            {children}
        </ThemeContext.Provider>
    );
}

export const useTheme = () => useContext(ThemeContext);

import { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { supabase } from '../services/supabase';
import { useAuth } from '../context/AuthContext';
import SEO from '../components/SEO';

export default function ComparisonPage() {
    const { user } = useAuth();
    const [savedIdeas, setSavedIdeas] = useState([]);
    const [selectedIds, setSelectedIds] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchVault = async () => {
            setLoading(true);
            // Fetch saved ideas
            const { data, error } = await supabase
                .from('user_saved_ideas')
                .select(`
                    id,
                    income_ideas (
                        id,
                        title,
                        slug,
                        initial_investment_min,
                        initial_investment_max,
                        monthly_income_min,
                        monthly_income_max,
                        time_to_first_income_days,
                        success_rate_percentage,
                        risk_level,
                        effort_level,
                        categories (name, icon)
                    )
                `)
                .eq('user_id', user.id);

            if (data) {
                setSavedIdeas(data.map(item => item.income_ideas));
            }
            setLoading(false);
        };

        if (user) fetchVault();
    }, [user]);

    const toggleSelection = (id) => {
        if (selectedIds.includes(id)) {
            setSelectedIds(prev => prev.filter(item => item !== id));
        } else {
            if (selectedIds.length >= 3) {
                alert("You can compare up to 3 blueprints at a time.");
                return;
            }
            setSelectedIds(prev => [...prev, id]);
        }
    };

    const getComparisonData = () => {
        return savedIdeas.filter(idea => selectedIds.includes(idea.id));
    };

    const formatCurrency = (amount) => {
        if (amount >= 100000) return `‚Çπ${(amount / 100000).toFixed(1)}L`;
        if (amount >= 1000) return `‚Çπ${(amount / 1000).toFixed(1)}k`;
        return `‚Çπ${amount}`;
    };

    const comparedItems = getComparisonData();

    return (
        <div className="min-h-screen bg-cream-50 pt-32 pb-20 px-4">
            <SEO title="Asset Comparison Matrix | Silent Money" />
            <div className="max-w-7xl mx-auto">
                <header className="mb-12 text-center">
                    <h1 className="text-4xl font-black text-charcoal-950 mb-4 tracking-tighter">
                        Tactical <span className="text-primary-600">Comparison</span>
                    </h1>
                    <p className="text-charcoal-500 font-medium">
                        Analyze your saved blueprints side-by-side to optimize your portfolio.
                    </p>
                </header>

                {/* Selection Tray */}
                <div className="card mb-12 border-none shadow-xl p-6 bg-white">
                    <h2 className="text-[10px] font-black text-charcoal-400 uppercase tracking-widest mb-4">Select Assets from Vault (Max 3)</h2>
                    <div className="flex flex-wrap gap-4">
                        {loading ? (
                            <div className="text-sm text-charcoal-400">Loading vault...</div>
                        ) : savedIdeas.length === 0 ? (
                            <div className="text-sm text-charcoal-400">No assets in vault. Save some ideas first.</div>
                        ) : (
                            savedIdeas.map(idea => (
                                <button
                                    key={idea.id}
                                    onClick={() => toggleSelection(idea.id)}
                                    className={`px-4 py-3 rounded-xl border-2 text-sm font-bold transition-all flex items-center gap-2 ${selectedIds.includes(idea.id)
                                        ? 'border-primary-600 bg-primary-50 text-primary-700'
                                        : 'border-charcoal-100 text-charcoal-600 hover:border-charcoal-300'
                                        }`}
                                >
                                    <span className="text-lg">{idea.categories?.icon || 'üìÑ'}</span>
                                    {idea.title}
                                    {selectedIds.includes(idea.id) && <span className="text-primary-600">‚úì</span>}
                                </button>
                            ))
                        )}
                    </div>
                </div>

                {/* Comparison Grid */}
                {comparedItems.length > 0 && (
                    <div className="overflow-x-auto pb-8">
                        <div className="min-w-[800px] grid grid-cols-[200px_repeat(3,1fr)] gap-0 border border-charcoal-200 rounded-3xl overflow-hidden bg-white shadow-2xl">

                            {/* Header Row */}
                            <div className="p-6 bg-charcoal-50 border-r border-b border-charcoal-100 flex items-center font-black text-charcoal-400 uppercase tracking-widest text-xs">
                                Metric
                            </div>
                            {comparedItems.map(item => (
                                <div key={item.id} className="p-6 bg-charcoal-50 border-b border-charcoal-100 flex flex-col items-center text-center">
                                    <div className="text-3xl mb-2">{item.categories?.icon}</div>
                                    <h3 className="font-black text-charcoal-900 text-lg leading-tight mb-2">{item.title}</h3>
                                    <Link to={`/ideas/${item.slug}`} className="text-[10px] font-black text-primary-600 uppercase tracking-widest hover:underline">View Blueprint ‚Üí</Link>
                                </div>
                            ))}
                            {/* Fill empty columns if less than 3 */}
                            {[...Array(3 - comparedItems.length)].map((_, i) => (
                                <div key={i} className="p-6 bg-charcoal-50 border-b border-charcoal-100 hidden md:block" />
                            ))}


                            {/* Capital Row */}
                            <div className="p-6 border-r border-b border-charcoal-100 font-bold text-charcoal-600 flex items-center">
                                Capital Required
                            </div>
                            {comparedItems.map(item => (
                                <div key={item.id} className="p-6 border-b border-charcoal-100 text-center flex items-center justify-center font-black text-charcoal-900 text-xl">
                                    {formatCurrency(item.initial_investment_min)} - {formatCurrency(item.initial_investment_max)}
                                </div>
                            ))}
                            {[...Array(3 - comparedItems.length)].map((_, i) => <div key={i} className="border-b border-charcoal-100 hidden md:block" />)}

                            {/* Income Row */}
                            <div className="p-6 border-r border-b border-charcoal-100 font-bold text-charcoal-600 flex items-center">
                                Monthly Yield
                            </div>
                            {comparedItems.map(item => (
                                <div key={item.id} className="p-6 border-b border-charcoal-100 text-center flex items-center justify-center font-black text-emerald-600 text-xl bg-emerald-50/10">
                                    {formatCurrency(item.monthly_income_min)} - {formatCurrency(item.monthly_income_max)}
                                </div>
                            ))}
                            {[...Array(3 - comparedItems.length)].map((_, i) => <div key={i} className="border-b border-charcoal-100 hidden md:block" />)}

                            {/* ROI Speed */}
                            <div className="p-6 border-r border-b border-charcoal-100 font-bold text-charcoal-600 flex items-center">
                                Time to First ‚Çπ
                            </div>
                            {comparedItems.map(item => (
                                <div key={item.id} className="p-6 border-b border-charcoal-100 text-center flex items-center justify-center font-bold text-charcoal-800">
                                    {item.time_to_first_income_days} Days
                                </div>
                            ))}
                            {[...Array(3 - comparedItems.length)].map((_, i) => <div key={i} className="border-b border-charcoal-100 hidden md:block" />)}

                            {/* Success Probability */}
                            <div className="p-6 border-r border-b border-charcoal-100 font-bold text-charcoal-600 flex items-center">
                                Success Rate
                            </div>
                            {comparedItems.map(item => (
                                <div key={item.id} className="p-6 border-b border-charcoal-100 text-center flex items-center justify-center">
                                    <div className="w-16 h-16 rounded-full border-4 border-primary-100 flex items-center justify-center font-black text-primary-700 relative">
                                        {item.success_rate_percentage}%
                                        <svg className="absolute inset-0 w-full h-full -rotate-90 text-primary-600" viewBox="0 0 36 36">
                                            <path
                                                d="M18 2.0845 a 15.9155 15.9155 0 0 1 0 31.831 a 15.9155 15.9155 0 0 1 0 -31.831"
                                                fill="none"
                                                stroke="currentColor"
                                                strokeWidth="3"
                                                strokeDasharray={`${item.success_rate_percentage}, 100`}
                                            />
                                        </svg>
                                    </div>
                                </div>
                            ))}
                            {[...Array(3 - comparedItems.length)].map((_, i) => <div key={i} className="border-b border-charcoal-100 hidden md:block" />)}

                            {/* Risk / Effort */}
                            <div className="p-6 border-r border-charcoal-100 font-bold text-charcoal-600 flex items-center">
                                Risk / Effort
                            </div>
                            {comparedItems.map(item => (
                                <div key={item.id} className="p-6 text-center flex flex-col items-center justify-center gap-2">
                                    <span className={`px-3 py-1 rounded-lg text-xs font-black uppercase tracking-widest ${item.risk_level === 'high' ? 'bg-red-100 text-red-700' :
                                            item.risk_level === 'medium' ? 'bg-yellow-100 text-yellow-700' : 'bg-green-100 text-green-700'
                                        }`}>
                                        {item.risk_level} Risk
                                    </span>
                                    <span className="text-xs font-bold text-charcoal-500 uppercase">{item.effort_level} Effort</span>
                                </div>
                            ))}
                            {[...Array(3 - comparedItems.length)].map((_, i) => <div key={i} className="hidden md:block" />)}

                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

